import React, {useState, useContext} from 'react';
//import RandomUser from '../apis/RandomUser';
import axios from 'axios';
import EmployeeFinder from '../apis/EmployeeFinder';
import { EmployeesContext } from '../contexts/EmployeesContext';

const FetchEmployees = () => {
  const { addEmployees } = useContext(EmployeesContext);

const handleClick = async (e) => {
  e.preventDefault();
    randomUserGenerator();


};

const randomUserGenerator = async () => {
  fetch('https://randomuser.me/api')
  .then((response) => {
      return response.json()
  }).then((data) => {
      console.log(data)
      const firstName = data.results[0].name.first;
      const lastName = data.results[0].name.last;
      console.log(firstName);

      const finishCreatingData = async () => {
        try {
          const response = await EmployeeFinder.post('/', {
        first_name: firstName,
        last_name: lastName
      });
      addEmployees(response.data.data.employee)
      console.log(response);
        } catch (error) {
            console.log(error);
        }


    }
    finishCreatingData();
  })
  }



  return (
    <div className="mb-4">
      <form action="">
        <div className="form-row">
            <button onClick={handleClick} type="button" className="btn btn-primary">Fetch Employees</button>
          </div>
        </form>
      </div>
    );
};

export default FetchEmployees;
