import React, { useContext, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { EmployeesContext } from '../contexts/EmployeesContext';
import EmployeeFinder from '../apis/EmployeeFinder';

const EmployeeDetailPage = () => {
    const { id } = useParams()
    const { selectedEmployee, setSelectedEmployee } = useContext(EmployeesContext);

    useEffect(() => {
        const fetchData = async () => {
        try {
            const response = await EmployeeFinder.get(`/${id}`);
            console.log(response);
            setSelectedEmployee(response.data.data.employee);

        } catch (error) {
            console.log(error);
        }
        };
        fetchData();
    }, []);

    return (

        <div className="list-group">
                <table className="table table-hover table-striped">
                    <thead>
                        <tr className=".bg-primary">
                            <th scope="col">Name</th>
                            <th scope="col">Email</th>
                            <th scope="col">City</th>
                            <th scope="col">Country</th>
                        </tr>
                    </thead>
                    <tbody>
                                <tr>
                                    <td>{selectedEmployee.first_name} {selectedEmployee.last_name}</td>
                                    <td>{selectedEmployee.email}</td>
                                    <td>{selectedEmployee.city}</td>
                                    <td>{selectedEmployee.country}</td>
                                </tr>
                            )
                        })}
                    </tbody>
                </table>
            </div>
    )
}

export default EmployeeDetailPage;
