import React, { useContext, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { EmployeesContext } from '../contexts/EmployeesContext';
import EmployeeFinder from '../apis/EmployeeFinder';

const EmployeeDetailPage = () => {
    const { id } = useParams()
    const { selectedEmployee, setSelectedEmployee } = useContext(EmployeesContext);

    useEffect(() => {
        const fetchData = async () => {
        try {
            const response = await EmployeeFinder.get(`/${id}`);
            console.log(response);
            setSelectedEmployee(response.data.data.employee);

        } catch (error) {
            console.log(error);
        }
        };
        fetchData();
    }, []);

    return (
        <container>
        <div>
            {selectedEmployee && (
            <form method="post">
                <div row>
                <div class="row">
                    <div class="col-md-3">
                        <div class="profile-img">
                            <img src={selectedEmployee.picture_large} alt=""/>
                        </div>
                    </div>
                    <div class="col-md-4">
                        <div class="profile-head">
                                    <h4>
                                        {selectedEmployee.first_name} {selectedEmployee.last_name}
                                    </h4>
                        </div>
                    </div>
                </div>
                <div class="row">
                    <div class="col-md-3">
                    </div>
                    <div class="col-md-6">

                                        <div class="row">
                                            <div class="col-md-5">
                                                <label>Email</label>
                                            </div>
                                            <div class="col-md-5">
                                                <p>{selectedEmployee.email}</p>
                                            </div>
                                        </div>
                                        <div class="row">
                                            <div class="col-md-5">
                                                <label>Address</label>
                                            </div>
                                            <div class="col-md-5">
                                                <p>{selectedEmployee.street_number} {selectedEmployee.street_name}</p>
                                                <p>{selectedEmployee.city}, {selectedEmployee.state} {selectedEmployee.postal_code}</p>
                                                <p>{selectedEmployee.country}</p>
                                            </div>
                                        </div>
                                        <div class="row">
                                            <div class="col-md-5">
                                                <label>Phone</label>
                                            </div>
                                            <div class="col-md-5">
                                                <p>{selectedEmployee.phone_number}</p>
                                            </div>
                                        </div>
                                        <div class="row">
                                            <div class="col-md-5">
                                                <label>Date of Birth</label>
                                            </div>
                                            <div class="col-md-5">
                                                <p>{selectedEmployee.date_of_birth}</p>
                                            </div>
                                        </div>
                                        <div class="row">

                        </div>
                    </div>
                </div>
                </div>
            </form>  
            )} 
        </div>  
        </container>      

    )
}

export default EmployeeDetailPage;
